import React, { useEffect, useRef } from 'react';
import { Graph } from '@antv/x6';
import { Stencil } from '@antv/x6-plugin-stencil';
import { basicNodes, containerNodes } from '../config/componentConfig';
import '../styles/stencil.css';

interface ComponentPanelProps {
  graph: Graph;
}

const ComponentPanel: React.FC<ComponentPanelProps> = ({ graph }) => {
  const stencilContainer = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (!stencilContainer.current) return;

    const stencil = new Stencil({
      title: '组件库',
      target: graph,
      stencilGraphWidth: 200,
      stencilGraphHeight: 500,
      collapsable: true,
      groups: [
        {
          name: 'basic',
          title: '基础组件',
          graphHeight: 250,
          layoutOptions: {
            columns: 2,
            marginX: 10,
            marginY: 10,
          },
        },
        {
          name: 'container',
          title: '容器组件',
          graphHeight: 250,
          layoutOptions: {
            columns: 1,
            marginX: 10,
            marginY: 10,
          },
        },
      ],
      getDragNode: (node) => node.clone(),
    });

    stencilContainer.current.appendChild(stencil.container);

    // 添加基础组件
    const basicStencilNodes = basicNodes.map((node) => {
      console.log('Creating basic node:', node);
      return graph.createNode(node);
    });
    console.log('Loading basic nodes:', basicStencilNodes);
    stencil.load(basicStencilNodes, 'basic');

    // 添加容器组件
    const containerStencilNodes = containerNodes.map((node) => {
      console.log('Creating container node:', node);
      return graph.createNode(node);
    });
    console.log('Loading container nodes:', containerStencilNodes);
    stencil.load(containerStencilNodes, 'container');

    return () => {
      stencil.dispose();
    };
  }, [graph]);

  return (
    <div 
      ref={stencilContainer} 
      style={{ 
        width: '240px',
        height: '100%',
        border: '1px solid #f0f0f0',
      }} 
    />
  );
};

export default ComponentPanel;
