export interface ComponentConfig {
  type: string;
  title: string;
  icon?: React.ReactNode;
  defaultProps: Record<string, any>;
}

export const basicComponents: ComponentConfig[] = [
  {
    type: 'Button',
    title: '按钮',
    defaultProps: {
      text: '按钮',
      type: 'primary',
      size: 'middle'
    }
  },
  {
    type: 'Input',
    title: '输入框',
    defaultProps: {
      placeholder: '请输入',
      allowClear: true
    }
  },
  {
    type: 'Select',
    title: '下拉选择',
    defaultProps: {
      placeholder: '请选择',
      options: [
        { label: '选项1', value: '1' },
        { label: '选项2', value: '2' }
      ]
    }
  },
  {
    type: 'Switch',
    title: '开关',
    defaultProps: {
      defaultChecked: false
    }
  }
];
